#include <stdio.h>
#include "libTest.h"

bool testIniToolsHandler(char*funcData,char*key,char*value) {
    char **data = (char**)funcData;
    *data = strdup(value);
    return true;
}
bool testIniTools(OBDIFace* iface) {
    {
        char *fname;
        FILE *f = tmpFile(&fname);
        fprintf(f,"k=v\n");
        fprintf(f,"k1=v\n");
        char bigKey[] = "";
        fprintf(f,"k2=%s\n",bigKey);
        fclose(f);
        char *value;
        assert(parse_ini_file(fname, testIniToolsHandler, &value));
        assert(strcmp(value,bigKey) == 0);
    }
    {
        char *fname;
        FILE *f = tmpFile(&fname);
        fprintf(f,"k=v\n");
        fprintf(f,"k1=v\n");
        char bigKey[] = "";
        fprintf(f,"k2=%s",bigKey);
        fclose(f);
        char *value;
        assert(parse_ini_file(fname, testIniToolsHandler, &value));
        assert(strcmp(value,bigKey) == 0);
    }
    return true;
}
